new FormControlController(this, {
    form: input => {
      // Buttons support a form attribute that points to an arbitrary form, so if this attribute is set we need to query
      // the form from the same root using its id
      if (input.hasAttribute('form')) {
        const doc = input.getRootNode() as Document | ShadowRoot;
        const formId = input.getAttribute('form')!;
        return doc.getElementById(formId) as HTMLFormElement;
      }

      // Fall back to the closest containing form
      return input.closest('form');
    },
    assumeInteractionOn: ['click']
  })

new FormControlController(this, {
    value: (control: CpsCheckbox) => (control.checked ? control.value || 'on' : undefined),
    defaultValue: (control: CpsCheckbox) => control.defaultChecked,
    setValue: (control: CpsCheckbox, checked: boolean) => (control.checked = checked)
  })

